import React, { useState } from 'react';
import P1 from '../images/n2.jpg';
import {Link, useNavigate,useLocation } from "react-router-dom";
import { FirebaseConfig } from "./FirebaseConfig";
import { initializeApp } from "firebase/app";
import {getAuth,signInWithEmailAndPassword,createUserWithEmailAndPassword } from "firebase/auth";

const Login = () => {
  const app = initializeApp(FirebaseConfig);
  const navigate =useNavigate();
  const location = useLocation();
  const page =location.pathname==='/login' ? true:false;
  const [email,setEmail]=useState('');
  const [password,setpassword]=useState('');
  const [isuserexist,setuserexist]=useState(false);
  const [isEmailUsed,setIsEmailUsed]=useState(false);
  const auth= getAuth();
  const ctaClickHandler
    = (e) => {
    e.preventDefault();
    if (page) {
      signInWithEmailAndPassword(auth, email, password)
        .then((auth) => {
          if (auth) {
            navigate("/dashboard");
          }
        })
        .catch((error) => setuserexist(true));
      /* user not found */
    }else{
      createUserWithEmailAndPassword(auth,email,password).then(auth=>{
        if (auth) {
          navigate('/dashboard')
        }
      })
      .catch(error=>console.log(error))
    }
    
  };

  const emailonchange=(e)=>{
setEmail(e.target.value)
  }
  const passwordonchange = (e) => {
     setpassword(e.target.value);
  };
   
    return (
      <div
        style={{
          backgroundImage: `URL(${P1})`,
        }}
        className=" h-screen"
      >
        <div>
          <div class="  justify-center md:w-96 h-1/2 md:mx-auto bg-black flex flex-col  w-full md:py-8 pt-8  md:mt-0">
            <p class="leading-relaxed mb-5 text-white text-3xl px-3">
              {page ? "Sign-In" : "Register Now"}
            </p>
            <div class="relative mb-4"></div>
            <div class="relative mb-4">
              <input
                type="email"
                id="email"
                value={email}
                onChange={emailonchange}
                name="email"
                placeholder="Email"
                class="w-full md:w-72 ml-3  bg-gray-600 rounded border border-gray-300 focus:border-green-950 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-white py-1  leading-8 transition-colors duration-200 ease-in-out placeholder-white"
              />
            </div>
            <div class="relative mb-4">
              <input
                type="password"
                id="password"
                value={password}
                onChange={passwordonchange}
                name="password"
                placeholder="password"
                class="w-full md:w-72 ml-3  bg-gray-600 rounded border border-gray-300 focus:border-green-950 focus:ring-2 focus:ring-indigo-200 text-base outline-none text-white py-1  leading-8 transition-colors duration-200 ease-in-out placeholder-white"
              />
            </div>
            <button
              class="text-white bg-green-950 border-0 py-2 px-1 focus:outline-none hover:bg-transparent rounded text-lg md:w-40 ml-3"
              onClick={ctaClickHandler}
            >
              {page ? "Sign-In" : "Register Now"}
            </button>
            {page && (
              <div className="form-check ml-3 pt-3">
                <input
                  className="form-check-input"
                  type="checkbox"
                  value=""
                  id="flexCheckDefault"
                />
                <label
                  className="form-check-label text-white"
                  htmlFor="flexCheckDefault"
                >
                  Remember Me
                </label>
              </div>
            )}{" "}
            {isuserexist && (
              <p className="text-red-700 font-extrabold  text-4xl ml-3 pt-3">
                User doesnt exist
              </p>
            )}
            {isuserexist && (
              <p className="text-red-700 font-extrabold  text-4xl ml-3 pt-3">
                Email AUsed
              </p>
            )}
            <p className="text-xs ml-3 pt-3">
              {page ? "new to tetflix?" : "Existing User."}
              <Link
                to={page ? "/register" : "/login"}
                href="#"
                className=" text-sm text-white font-bold"
              >
                {page ? "Sing-up Now" : "Sing-in Now"}
              </Link>
            </p>
          </div>
        </div>
      </div>
    );
};

export default Login;
