import React from 'react';
import p from '../images/n1.jpg';

const HomeBanner = () => {
    return (
      <div>
        <div
          className="w-full  h-screen object-cover z-5 flex  opacity-30"
          style={{ backgroundImage: `URL(${p})` }}
        >
          <div className="form-control mx-auto justify-center z-20 ">
            <h1 className="text-center text-4xl text-white  spacing">
              Unlimited movies TV
              <br />
              shows and more.
            </h1>
            <p className="text-center text-xl text-white ">
              Watch anywhere.Cancel anytime
            </p>
            <p className="text-center text-sm text-white ">
             Ready to watch.Enter Your Email to Creat
            </p>
            <label className="input-group">
              <input
                type="text"
                placeholder="email address"
                className="input input-bordered"
              />
              <button className="btn bg-green-700">Get Started</button>
            </label>
          </div>
        </div>
      </div>
    );
};

export default HomeBanner;

