import React, { useEffect, useState } from "react";

import Youtube from "react-youtube";
import movieTrailer from "movie-trailer";
import { upcoming } from "./Api4";

const Upcoming = ({ param }) => {
  const [UP, setUP] = useState([]);
  
  const [trailer, setTrailer] = useState("");
  

  const handleClick = (movie) => {
    if (trailer) {
      setTrailer("");
    } else {
      movieTrailer(movie?.name || movie?.title || movie?.original_title || "")
        .then((url) => {
          const urlParams = new URLSearchParams(new URL(url).search);
          setTrailer(urlParams.get("v"));
        })
        .catch((error) => console.log(error));
    }
  };
  
    const opts = {
      height: "400",
      width: "98%",
      playerVars: {
        autoplay: 1,
      },
    };

  useEffect(() => {
    upcoming(param).then((res) => setUP(res.data.results));
  }, []);

  return (
    <div className="bg-black">
      
      <section class=" bg-black body-font">
        <h2 className="text-white p-5 font-bold text-2xl pt-16">Upcoming</h2>
        <div class="container px-5 py-10  rounded-xl mx-auto">
          <div class="flex flex-wrap -m-4">
            {UP.slice(0, 50).map((item) => (
              <div class="lg:w-1/4 md:w-1/2 shadow-lg mb-16 rounded-xl  shadow-rose-700 p-4 w-full">
                <a class="block relative h-48 rounded overflow-hidden">
                  <img
                    class="object-cover object-center w-full h-full block"
                    src={`https://image.tmdb.org/t/p/original${item.poster_path}`}
                    alt={item.title}
                  />
                </a>
                <div class="mt-4">
                  <h3 class="text-white text-xs tracking-widest title-font mb-1">
                    Air: {item.release_date}
                  </h3>
                  <h2 class="text-rose-600 title-font text-lg font-bold">
                    {item.title}
                  </h2>
                  <p class="mt-1 text-rose-200">Ratings:{item.vote_average}</p>
                      <button
                  onClick={() => handleClick(item)}
                  className="bg-rose-600 mt-2 hover:bg-black text-white text-sm p-2 shadow-lg shadow-rose-800 rounded-lg"
                >
                  <label className="btn bg-transparent border-none text-white swap swap-rotate">
  
  
  <input type="checkbox" />
  
 
  <p className="swap-off bg-transparent">Watch tailer</p>
  
  <svg className="swap-on fill-current" xmlns="http://www.w3.org/2000/svg" width="32" height="32" viewBox="0 0 512 512"><polygon points="400 145.49 366.51 112 256 222.51 145.49 112 112 145.49 222.51 256 112 366.51 145.49 400 256 289.49 366.51 400 400 366.51 289.49 256 400 145.49"/></svg>
  
</label>
                </button>
            
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>
    </div>
  );
};

export default Upcoming;
